import {createContext, useContext, useEffect, useMemo, useReducer, useRef, useState} from "react";

export const initialState = { theme: "", data: [] };

export const DoctStates = createContext(undefined);

const themes = {
    dark : {
        theme: false,
        bgcolor: 'black',
        color: 'white'
    },
    light : {
        theme: true,
        bgcolor: 'white',
        color: 'black'
    }
}

export const ContextProvider = ({ children }) => {
    const [doctList, setDoctList] = useState([]);
    const url = "https://jsonplaceholder.typicode.com/users/";
    const [themeState, themeDispatch] = useReducer()

    const memoizedValue = useMemo(() => {
        return { doctList, setDoctList };
    }, [doctList]);

    useEffect(() => {
        const fetchDoct = async () => {
            let res = await fetch(url);
            let data = await res.json();
            setDoctList(data.results);
        };
        fetchDoct();
    }, []);

    return (
        <DoctStates.Provider value={memoizedValue}>
            {children}
        </DoctStates.Provider>
    );
};

export default ContextProvider

export const useDoctStates = () => useContext(DoctStates)
